import React, { useState } from 'react';
/* eslint-disable jsx-a11y/no-noninteractive-element-to-interactive-role */
const loopImage = require('../../images/3641364.png');

export default function SearchBar({ search }) {
  const [className, setClassName] = useState('search__bar');

  // const showSearchBar = () => {
  //   setClassName('search__bar search__bar--active');
  // };

  return (
    <div className="search">
      <img className="search__img" src={loopImage} alt="loop__image" onClick={() => { setClassName('search__bar search__bar--active'); }} onKeyDown={setClassName('search__bar search__bar--active')} role="button" tabIndex={0} />
      <input className={className} type="text" onChange={(event) => search(event.target.value)} />
    </div>
  );
}
