import * as actions from './actionTypes';

export default function reducer(action, state = []) {
  localStorage.clear();
  switch (action.type) {
    case actions.COIN_ADD:
      return [...state, {
        name: action.payload.name,
        count: action.payload.count,
        priceUsd: action.payload.priceUsd,
        totalpriceUsd: action.payload.totalpriceUsd,
        key: action.payload.key,
      }];
    case actions.COIN_REMOVE: {
      return state.filter((task) => action.payload.key !== task.key);
    }
    default:
      return state;
  }
}
