import axiosInstance from './axiosInstance';

const requestOptions = {
  offset: 2,
};

export default async function getInfo(url) {
  try {
    const info = await axiosInstance.get(`${url}`, requestOptions);
    return info.data.data;
  } catch (err) {
    return getInfo(url);
  }
}
