import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';
import fixed from '../../Functions/fixed';
import showInputForCount from '../../Functions/showInputForCount';
import * as actions from '../../Store/actions';

function CoinBlock({ coins }) {
  const {
    id, rank, name, symbol, priceUsd, marketCapUsd, vwap24Hr, supply, volumeUsd24Hr, changePercent24Hr,
  } = coins;

  const dispatch = useDispatch();

  const [inputValue, setInputValue] = useState(0);

  return (
    <div className="coins-list">
      {
                  coins.map((coin) => (
                    <div className="coin" id={id} key={id} onClick={showInputForCount} onKeyDown={showInputForCount} role="button" tabIndex={0}>
                      <div className="coin__info">
                        {rank}
                      </div>
                      <Link className="coin__info" to={`/infopage/${coin.id}`}>
                        <div className="coin__info-name">
                          {name}
                        </div>
                        <div className="coin__info-symbol">
                          {symbol}
                        </div>
                      </Link>
                      <div className="coin__info">
                        $
                        {fixed(priceUsd)}
                      </div>
                      <div className="coin__info-sm">
                        $
                        {fixed(marketCapUsd)}
                      </div>
                      <div className="coin__info-sm">
                        $
                        {fixed(vwap24Hr)}
                      </div>
                      <div className="coin__info-sm">
                        $
                        {fixed(supply)}
                      </div>
                      <div className="coin__info-sm">
                        $
                        {fixed(volumeUsd24Hr)}
                      </div>
                      <div className="coin__info">
                        {fixed(changePercent24Hr)}
                        %
                      </div>
                      <div className="coin__form coin__form--display--none">
                        <input className="coin__form-input" type="number" onChange={(e) => setInputValue(e.target.value)} />
                        <button className="form-button form-button--color--green" onClick={() => { dispatch(actions.loadCoin(coin.id, inputValue)); }}>Add to basket</button>
                      </div>
                    </div>
                  ))
              }
    </div>
  );
}

export default CoinBlock;
