import React, { useState, useEffect } from 'react';
import fixed from '../../Functions/fixed';

export default function Information({ id, coins }) {
  const [info, setInfo] = useState('');

  useEffect(() => {
    setInfo(coins.find((coin) => coin.id === id));
  }, [id, coins]);

  return (
    <div className="info-page__list">
      <div className="info-page__list-item">
        <div>
          {info.name}
        </div>
        <div>
          {info.symbol}
        </div>
      </div>
      <div className="info-page__list-item">
        price: $
        {fixed(info.priceUsd)}
      </div>
      <div className="info-page__list-item">
        marketCap: $
        {fixed(info.marketCapUsd)}
      </div>
      <div className="info-page__list-item">
        vwap (24Hr): $
        {fixed(info.vwap24Hr)}
      </div>
      <div className="info-page__list-item">
        supply: $
        {fixed(info.supply)}
      </div>
      <div className="info-page__list-item">
        maxSupply: $
        {fixed(info.maxSupply)}
      </div>
      <div className="info-page__list-item">
        volume (24Hr): $
        {fixed(info.volumeUsd24Hr)}
      </div>
      <div className="info-page__list-item">
        changePercent (24Hr):
        {' '}
        {fixed(info.changePercent24Hr)}
        %
      </div>
    </div>
  );
}
