import React, { useEffect, useState } from 'react';

function Pagination({ coinsPerPage, totalCoins, pagination }) {
  const [prevElement, setPrevElement] = useState('');
  const [visiblePages, setVisiblePages] = useState([]);
  const [pageNumbers, setPageNumbers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    const pageArray = [];
    for (let i = 1; i <= Math.ceil(totalCoins / coinsPerPage); i += 1) {
      pageArray.push(i);
    }
    setPageNumbers(pageArray);
  }, [coinsPerPage, totalCoins]);

  useEffect(() => {
    const newArray = pageNumbers.slice();
    newArray.splice(3, newArray.length - 4, '...');
    setVisiblePages(newArray);
  }, [pageNumbers]);

  const select = (event, pageNumber) => {
    if (pageNumber === '...') return;
    if (prevElement === '') {
      event.target.classList.add('pagination__list-item--selected');
    } else {
      prevElement.classList.remove('pagination__list-item--selected');
      event.target.classList.add('pagination__list-item--selected');
    }
    setPrevElement(event.target);
  };

  useEffect(() => {
    const newArray = pageNumbers.slice();
    if (currentPage === '...') return;
    pagination(currentPage);
    if (currentPage === 1 || currentPage === 2) {
      newArray.splice(3, newArray.length - 4, '...');
      setVisiblePages(newArray);
    } else if (currentPage === newArray.length || currentPage === newArray.length - 1) {
      newArray.splice(1, newArray.length - 4, '...');
      setVisiblePages(newArray);
    } else {
      const previousPage = currentPage - 1;
      const secondPage = currentPage + 1;
      if (newArray.length - (secondPage + 1) !== 0) newArray.splice(secondPage, newArray.length - (secondPage + 1), '...');
      if (previousPage - 2 !== 0) newArray.splice(1, previousPage - 2, '...');
      setVisiblePages(newArray);
    }
  }, [currentPage, pageNumbers]);

  if (pageNumbers.length === 1) return null;

  return (
    <div className="pagination">
      <ul className="pagination__list">
        {
                    visiblePages.map((pageNumber) => {
                      console.log(pageNumber);
                      return (
                        <li
                          className="pagination__list-item"
                          // eslint-disable-next-line react/no-array-index-key
                          key={pageNumber}
                          onClick={(event) => {
                            select(event, pageNumber);
                            setCurrentPage(pageNumber);
                          }}
                          onKeyDown={() => {
                            setCurrentPage(pageNumber);
                          }}
                          // eslint-disable-next-line jsx-a11y/no-noninteractive-element-to-interactive-role
                          role="button"
                          tabIndex={0}
                        >
                          {pageNumber}
                        </li>
                      );
                    })
                }
      </ul>
    </div>
  );
}

export default Pagination;
