// import PropTypes from 'prop-types';
import { getInfo } from '../API/api';
import fixed from './fixed';
import * as actions from '../Store/actions';

async function handleCoinSubmit(id, dispatch, target) {
  const coininf = await getInfo(`assets/${id}`);
  const input = target.querySelector('input');
  const count = input.value;
  input.value = '';
  const price = fixed(coininf.priceUsd);
  const totalPrice = fixed((price * count));
  const { name } = coininf;

  dispatch(actions.addCoin({
    title: {
      name,
      count,
      priceUsd: price,
      totalpriceUsd: totalPrice,
      key: id,
    },
  }));
}

// handleCoinSubmit.propTypes = {
//   id: PropTypes.number,
//   dispatch: PropTypes.func,
//   target: PropTypes.element,
// };

export default handleCoinSubmit;
