function showInputForCount(event) {
  const input = event.currentTarget.querySelector('input');
  const button = event.currentTarget.querySelector('button');
  console.log(event.target.parentElement);
  console.log(event.target);
  const visible = event.target.parentElement.querySelectorAll('.coin__form');
  if (event.target === input || event.target === button || !event.target.parentElement.classList.contains('.coin__form--display--none')) return;
  visible.forEach((elem) => {
    if (!elem.classList.contains('coin__form--display--none')) {
      elem.classList.add('coin__form--display--none');
    }
  });
  event.target.parentElement.classList.remove('coin__form--display--none');
}

export default showInputForCount;
