import { getInfo } from "../API/api";
import { fixed } from "./fixed";
import * as actions from "../Store/actions";

export const handleCoinSubmit = async ( parentElement, dispatch, target ) => {
    let id = parentElement.id;
    let coininf = await getInfo(`assets/${id}`);
    let input = target.querySelector('input');
    let count = Number(input.value);
    let price = coininf.priceUsd * count;
    console.log(target);
    console.log(id);
    console.log(coininf);
    console.log(price);
    price = fixed(price);
    let name = coininf.name;
    dispatch(actions.addCoin({
      title: {
          name: name,
          count: count,
          priceUsd: price,
          key: id
      }
    }));
    
};
