import React, { useEffect, useState } from 'react';
// eslint-disable-next-line import/no-extraneous-dependencies
import {
  AreaChart, Area, YAxis, XAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer,
} from 'recharts';
import { getInfo } from '../../API/api';

function History({ id }) {
  const [history, SetHistory] = useState('');

  const selectValue = document.querySelector('.info-page__select').value;
  console.log(selectValue);

  const getHistory = async () => {
    const dateInformation = await getInfo(`assets/${id}/history?interval=d1`);
    const truncatedArray = dateInformation.map((dateinfo) => {
      const container = {};
      container.priceUsd = dateinfo.priceUsd;
      container.date = dateinfo.date.substr(0, 9);
      return container;
    });
    SetHistory(truncatedArray);
  };

  useEffect(() => {
    getHistory();
  }, []);

  return (
    <div className="chart">
      <ResponsiveContainer width="100%" height={300}>
        <AreaChart
          data={history}
          margin={{
            top: 10, right: 30, left: 0, bottom: 0,
          }}
        >
          <defs>
            <linearGradient id="colorPv" x1="0" y1="0" x2="0" y2="1">
              <stop offset="5%" stopColor="#82ca9d" stopOpacity={0.8} />
              <stop offset="95%" stopColor="#82ca9d" stopOpacity={0} />
            </linearGradient>
          </defs>
          <XAxis dataKey="date" />
          <YAxis />
          <CartesianGrid strokeDasharray="3 3" />
          <Tooltip />
          <Legend />
          <Area type="monotone" dataKey="priceUsd" stroke="#8884d8" fillOpacity={1} fill="url(#colorUv)" />
        </AreaChart>
      </ResponsiveContainer>
    </div>
  );
}

export default History;
