import axios from 'axios';

// eslint-disable-next-line import/prefer-default-export
export async function getInfo(url) {
  try {
    const info = await axios.get(`https://api.coincap.io/v2/${url}`);
    const json = await info.json();
    return json.data;
  } catch (err) {
    return err;
  }
}
