import { loadCoin } from './loadCoin';

function handleCoinSubmit(id, target) {
  const input = target.querySelector('input');
  const count = Number(input.value);
  if (count === 0 || count === ' ') return;
  loadCoin(id, count);
  input.value = '';
}

export default handleCoinSubmit;
