import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import * as actions from '../../Store/actions';

export default function Form({
  id, rateId, priceUsd, symbol,
}) {
  const [inputValue, setInputValue] = useState(0);
  const [message, setMessage] = useState('');
  const [totalPrice, setTotalPrice] = useState('');
  const [currencySymbol, setCurrencySymbol] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    setMessage(document.querySelector('.message'));
  }, []);

  const showTotalPrice = (value) => {
    setInputValue(value);
    if (Number.isNaN(value * priceUsd)) {
      setTotalPrice('');
      setCurrencySymbol('');
    } else {
      setTotalPrice(value * priceUsd);
      setCurrencySymbol(symbol);
    }
  };

  const checkForCorrect = (value) => {
    if (value < 0) setErrorMessage('Please, enter positive number');
    else setErrorMessage('');
  };

  const dispatch = useDispatch();
  return (
    <div className="form">
      <div className="input-container">
        <div className="errorMessage" aria-live="polite">{errorMessage}</div>
        <input
          className="form__input"
          type="number"
          onChange={(e) => {
            showTotalPrice(e.target.valueAsNumber);
            checkForCorrect(e.target.valueAsNumber);
          }}
        />
      </div>
      <button className="form__button" onClick={() => dispatch(actions.loadCoin(id, inputValue, message, rateId))}>
        <div className="form__button-text">Add to basket</div>
      </button>
      <div className="form__text">
        {currencySymbol}
        {' '}
        {totalPrice}
      </div>
    </div>
  );
}
