import axios from 'axios';

const axiosInstance = axios.create({
  baseURL: 'https://api.coincap.io/v2/',
});

axiosInstance.interceptors.response.use((response) => {
  let truncatedArray = [];
  if (response.data.data.length < 365 && response.data.data.length > 100) {
    truncatedArray = response.data.data.map((dateinfo) => {
      const container = {};
      container.priceUsd = dateinfo.priceUsd;
      container.date = dateinfo.date.substr(0, 10);
      return container;
    });
    return truncatedArray;
  } if (response.data.data.length > 365) {
    truncatedArray = response.data.data.map((dateinfo) => {
      const container = {};
      container.priceUsd = dateinfo.priceUsd;
      container.date = dateinfo.date.substr(0, 10);
      return container;
    });
    return truncatedArray;
  }
  return response;
});

export default axiosInstance;
