function showInputForCount(event) {
  console.log(event.currentTarget);
  const input = event.currentTarget.querySelector('input');
  const button = event.currentTarget.querySelector('button');
  const thisElement = event.target.parentElement.querySelector('.display');
  const visible = document.querySelectorAll('.display');
  const element = event.target.parentElement.querySelector('.none');
  if (event.target === input || event.target === button || !thisElement.classList.contains('none')) return;
  visible.forEach((elem) => {
    if (!elem.classList.contains('none')) {
      elem.classList.add('none');
    }
  });
  element.classList.remove('none');
}

export default showInputForCount;
