import React, { useEffect, useState } from 'react';
// eslint-disable-next-line import/no-extraneous-dependencies
import {
  AreaChart, Area, YAxis, XAxis, CartesianGrid, Tooltip, Legend,
} from 'recharts';
import { getInfo } from '../../API/api';

function History({ prodId }) {
  const [history, SetHistory] = useState('');

  const getHistory = async () => {
    SetHistory(await getInfo(`assets/${prodId}/history?interval=d1`));
  };

  useEffect(() => {
    getHistory();
  }, []);

  return (
    <div className="chart">
      <AreaChart
        width={700}
        height={400}
        data={history}
        margin={{
          top: 10, right: 30, left: 0, bottom: 0,
        }}
      >
        <defs>
          <linearGradient id="colorPv" x1="0" y1="0" x2="0" y2="1">
            <stop offset="5%" stopColor="#82ca9d" stopOpacity={0.8} />
            <stop offset="95%" stopColor="#82ca9d" stopOpacity={0} />
          </linearGradient>
        </defs>
        <XAxis dataKey="date" />
        <YAxis />
        <CartesianGrid strokeDasharray="3 3" />
        <Tooltip />
        <Legend />
        <Area type="monotone" dataKey="priceUsd" stroke="#8884d8" fillOpacity={1} fill="url(#colorUv)" />
      </AreaChart>
    </div>
  );
}

export default History;
