import * as actions from './actionTypes';

export const addCoin = (data) => ({
  type: actions.COIN_ADD,
  payload: {
    name: data.info.name,
    count: data.count,
    priceUsd: data.info.priceUsd,
    coinTotalPrice: data.coinTotalPrice,
    key: data.info.id,
  },
});
export const removeCoin = (id) => ({
  type: actions.COIN_REMOVE,
  payload: { id },
});
