import React from 'react';
import PropTypes from 'prop-types';

function Pagination({ coinsPerPage, totalCoins, pagination }) {
  const pageNumbers = [];

  for (let i = 1; i <= Math.ceil(totalCoins / coinsPerPage); i += 1) {
    pageNumbers.push(i);
  }

  const selected = (number, event) => {
    pagination(number);
    const selectedBlock = document.querySelector('.selected');
    if (selectedBlock) {
      selectedBlock.classList.remove('selected');
    }
    event.currentTarget.classList.add('selected');
  };

  return (
    <div className="container">
      <div className="row justify-content-center mt-2">
        {
                    pageNumbers.map((number) => (
                      <div className="col-lg-auto col-sm-auto" key={number} onClick={(event) => selected(number, event)} onKeyDown={(event) => selected(number, event)} role="button" tabIndex={0}>
                        <div>
                          {number}
                        </div>
                      </div>
                    ))
                }
      </div>
    </div>
  );
}

Pagination.propTypes = {
  coinsPerPage: PropTypes.number.isRequired,
  totalCoins: PropTypes.number.isRequired,
  pagination: PropTypes.func.isRequired,
};

export default Pagination;
