/* eslint-disable jsx-a11y/no-noninteractive-element-to-interactive-role */
import React from 'react';

const upArrowImage = require('../../images/down--v2.png');
const downArrowImage = require('../../images/down--v1.png');

export default function Header({ sort }) {
  const getId = (element) => {
    sort(element.id);
    if (element.classList.contains('top__list-item-img--hidden')) element.classList.remove('top__list-item-img--hidden');
    if (element.id === `${element.innerHTML} up`) {
      element.setAttribute('id', `${element.innerHTML} down`);
      element.setAttribute('src', `${downArrowImage}`);
    } else {
      element.setAttribute('id', `${element.innerHTML} up`);
      element.setAttribute('src', `${upArrowImage}`);
    }
  };

  return (
    <ul className="top" onClick={(event) => getId(event.target)} onKeyDown={(event) => sort(event.target.text)} role="button" tabIndex={0}>
      <div className="top__list">
        <li className="top__list-item" id="Rank">
          Rank
          {' '}
          <img className="top__list-item-img top__list-item-img--hidden" src="" alt="item-img" />
        </li>
        <li className="top__list-item" id="Name">
          Name
          {' '}
          <img className="top__list-item-img top__list-item-img--hidden" src="" alt="item-img" />
        </li>
        <li className="top__list-item" id="Price">
          Price
          {' '}
          <img className="top__list-item-img top__list-item-img--hidden" src="" alt="item-img" />
        </li>
        <li className="top__list-item-sm" id="MarketCap">
          MarketCap
          {' '}
          <img className="top__list-item-img top__list-item-img--hidden" src="" alt="item-img" />
        </li>
        <li className="top__list-item-sm" id="wap (24Hr)">
          wap (24Hr)
          {' '}
          <img className="top__list-item-img top__list-item-img--hidden" src="" alt="item-img" />
        </li>
        <li className="top__list-item-sm" id="Supply">
          Supply
          {' '}
          <img className="top__list-item-img top__list-item-img--hidden" src="" alt="item-img" />
        </li>
        <li className="top__list-item-sm" id="Volume (24Hr)">
          Volume (24Hr)
          {' '}
          <img className="top__list-item-img top__list-item-img--hidden" src="" alt="item-img" />
        </li>
        <li className="top__list-item" id="Change (24Hr)">
          Change (24Hr)
          {' '}
          <img className="top__list-item-img top__list-item-img--hidden" src="" alt="item-img" />
        </li>
      </div>
    </ul>
  );
}
