import React, { useState } from 'react';

export function HeaderWithSorting({
  sort, names, namesForSmallScreeen, ASCImage, DESCImage,
}) {
  const [prevElement, setPrevElement] = useState('');

  const showImage = (element) => {
    sort(element.id);
    if (element !== prevElement && prevElement) prevElement.lastChild.classList.add('top__list-item-img--hidden');
    setPrevElement(element);
    if (element.lastChild.classList.contains('top__list-item-img--hidden')) element.lastChild.classList.remove('top__list-item-img--hidden');
    if (element.id === `${element.getAttribute('name')} up`) {
      element.setAttribute('id', `${element.getAttribute('name')} down`);
      element.lastChild.setAttribute('src', ASCImage);
    } else {
      element.setAttribute('id', `${element.getAttribute('name')} up`);
      element.lastChild.setAttribute('src', DESCImage);
    }
  };

  if (namesForSmallScreeen) {
    return (
      <div className="top" onClick={(event) => showImage(event.target)} onKeyDown={(event) => sort(event.target.text)} role="button" tabIndex={0}>
        <ul className="top__list">
          {
                            // eslint-disable-next-line array-callback-return
                            names.forEach((name) => {
                              namesForSmallScreeen.map((namesm) => {
                                if (name === namesm) {
                                  return (
                                    <div className="top__list-item-sm" name={name} key={name}>
                                      {name}
                                      {' '}
                                      <img className="top__list-item-img top__list-item-img--hidden" alt="item-img" />
                                    </div>
                                  );
                                } return (
                                  <div className="top__list-item" name={name} key={name}>
                                    {name}
                                    {' '}
                                    <img className="top__list-item-img top__list-item-img--hidden" alt="item-img" />
                                  </div>
                                );
                              });
                            })
                        }
        </ul>
      </div>
    );
  } return (
    <div className="top" onClick={(event) => showImage(event.target)} onKeyDown={(event) => sort(event.target.text)} role="button" tabIndex={0}>
      <ul className="top__list">
        {
                            names.map((name) => (
                              <div className="top__list-item" name={name} key={name}>
                                {name}
                                {' '}
                                <img className="top__list-item-img top__list-item-img--hidden" alt="item-img" />
                              </div>
                            ))
                            }
      </ul>
    </div>
  );
}
