import * as actions from './actionTypes';
import { getInfo } from '../API/api';

export const loadCoin = (task) => (dispatch) => {
  // eslint-disable-next-line no-use-before-define
  dispatch(addCoin({
    info: getInfo(`assets/${task.title.id}`),
    count: task.title.count,
  }));
};
export const addCoin = (data) => ({
  type: actions.COIN_ADD,
  payload: {
    name: data.info.name,
    count: data.title.count,
    priceUsd: data.info.priceUsd,
    totalpriceUsd: data.info.totalpriceUsd,
  },
});
export const removeCoin = (id) => ({
  type: actions.COIN_REMOVE,
  payload: { id },
});
