function showInputForCount(targetElement) {
  let prevElement;
  if (prevElement !== targetElement) {
    if (prevElement) {
      prevElement.classList.add('coin__form--display--none');
    }
    prevElement = targetElement;
  } else return;
  const element = targetElement.querySelector('.coin__form');
  console.log(!element.classList.contain('coin__form--display--none'));
  if (!element.classList.contain('coin__form--display--none')) return;
  element.classList.remove('coin__form--display--none');
}

export default showInputForCount;
