import React from 'react';
// import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import fixed from '../../Functions/fixed';

function CoinBlock({
  coins, handleCoinSubmit, showInputForCount, clearText,
}) {
  return (
    <div className="coins-block">
      <div className="container" onClick={showInputForCount} onKeyDown={showInputForCount} role="button" tabIndex={0}>
        {
                    coins.map((coin) => (
                      <div className="row py-2 align-items-center" id={coin.id} key={coin.id}>
                        <div className="col-lg col-sm text-center">
                          {coin.rank}
                        </div>
                        <div className="col-lg col-sm text-center">
                          <Link to={`/infopage/${coin.id}`} style={{ textDecoration: 'none', color: 'white' }}>
                            <div>
                              {coin.name}
                            </div>
                            <div>
                              {coin.symbol}
                            </div>
                          </Link>
                        </div>
                        <div className="col-lg col-sm text-center">
                          $
                          {fixed(coin.priceUsd)}
                        </div>
                        <div className="col-lg col-sm text-center">
                          $
                          {fixed(coin.marketCapUsd)}
                        </div>
                        <div className="col-lg col-sm text-center">
                          $
                          {fixed(coin.vwap24Hr)}
                        </div>
                        <div className="col-lg col-sm text-center">
                          $
                          {fixed(coin.supply)}
                        </div>
                        <div className="col-lg col-sm text-center">
                          $
                          {fixed(coin.volumeUsd24Hr)}
                        </div>
                        <div className="col-lg col-sm text-center">
                          {fixed(coin.changePercent24Hr)}
                          %
                        </div>
                        <div className="col-lg-12 col-sm-12 text-center">
                          <div className="display none">
                            <input type="text" onBlur={clearText} />
                            <button onClick={handleCoinSubmit}>Add</button>
                          </div>
                        </div>
                      </div>
                    ))
                }
      </div>
    </div>
  );
}

// CoinBlock.propTypes = {
//   handleCoinSubmit: PropTypes.func.isRequired,
//   showInputForCount: PropTypes.func.isRequired,
//   clearText: PropTypes.func.isRequired,
// };

export default CoinBlock;
